import React, { useState } from 'react';
import './drinkCard.css';

const Drink = ({ drinks }) => {
  const [selectedDrink, setSelectedDrink] = useState(null);

  const handleDrinkClick = (drink) => {
    setSelectedDrink(drink);
  };

  return (
    <div>
      <div className="drink-container">
        {drinks.map((drink, index) => (
          <div key={index} className="drink-item" onClick={() => handleDrinkClick(drink)}>
            <h2>{drink.strDrink}</h2>
            <img src={drink.strDrinkThumb} alt={drink.strDrink} />
            <p>IBA: {drink.strIBA}</p>
            <p>Alcohol: {drink.strAlcoholic}</p>
            <p>Glass: {drink.strGlass}</p>
          </div>
        ))}
      </div>
      {selectedDrink && (
        <div className="selected-drink-container">
          <h3>{selectedDrink.strDrink}</h3>
          <img src={selectedDrink.strDrinkThumb} alt={selectedDrink.strDrink} />
          <p>Instructions: {selectedDrink.strInstructions}</p>
          {/* Add more details from the 'selectedDrink' object as needed */}
        </div>
      )}
    </div>
  );
};

export default Drink;
