import React, { useState, useEffect } from 'react';
import serviceApi from '../../services/Api';
import Slider from 'react-slick';
import Drink from '../../components/DrinkCard';
import Dropdown from 'react-bootstrap/Dropdown';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import loupeImage from '../../images/loupe.png';

const App = () => {
  const [drinks, setDrinks] = useState([]);
  const [drinkCateg, setDrinkCateg] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchDrinkCategory, setSearchDrinkCategory] = useState('');
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const drinksPerPage = 20;
  const breakpoint = 768;

  const fetchDrinks = async () => {
    try {
      const offset = (currentPage - 1) * drinksPerPage;

      if (searchDrinkCategory) {
        const categoryResponse = await serviceApi.searchDrinksCateg(searchDrinkCategory);
        setDrinks(categoryResponse.drinks || []);
      } else if (searchTerm) {
        const searchResponse = await serviceApi.searchDrinks(searchTerm);
        setDrinks(searchResponse.drinks || []);
      } else {
        const allResponse = await serviceApi.getAll();
        setDrinks(allResponse.drinks || []);
      }
    } catch (error) {
      console.error('Error fetching drinks:', error);
      setDrinks([]);
    }
  };

  useEffect(() => {
    fetchDrinks();
  }, [searchTerm, searchDrinkCategory]);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const carouselSettings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: breakpoint,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  const handleCategoryChange = (selectedCategory) => {
    setSearchDrinkCategory(selectedCategory);
    setSearchTerm('');
  };

  return (
    <div className='drinksPage'>
      <div className='drink-search'>
        <input
          type="text"
          placeholder="Enter drink name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <img src={loupeImage} alt="Search" className="search-icon" />
      </div>
      <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
          Select Category
        </Dropdown.Toggle>

        <Dropdown.Menu>
          {drinkCateg.map((category) => (
            <Dropdown.Item
              key={category.strCategory}
              onClick={() => handleCategoryChange(category.strCategory)}
            >
              {category.strCategory}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
      <div className={windowWidth >= breakpoint ? 'drink-container' : 'drink-carousel'}>
        {windowWidth < breakpoint ? (
          <Slider {...carouselSettings}>
            {drinks.map((drink, index) => (
              <Drink key={index} drink={drink} />
            ))}
          </Slider>
        ) : (
          drinks.map((drink, index) => (
            <Drink key={index} drink={drink} />
          ))
        )}
      </div>
    </div>
  );
};

export default App;
