import React, { useState } from 'react';
import './drinkCard.css'; // Import the CSS file

const Drink = ({ drinks }) => {
  const drinksPerPage = 10;
  const [currentPage, setCurrentPage] = useState(1);

  const lastDrinkIndex = currentPage * drinksPerPage;
  const firstDrinkIndex = lastDrinkIndex - drinksPerPage;
  const currentDrinks = drinks.slice(firstDrinkIndex, lastDrinkIndex);

  const totalPages = Math.ceil(drinks.length / drinksPerPage);

  const goToNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages));
  };

  const goToPrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  return (
    <div>
      <div className="drink-container">
        {currentDrinks.map((drink, index) => (
          <div key={index} className="drink-item">
            <h2>{drink.strDrink}</h2>
            <img src={drink.strDrinkThumb} alt={drink.strDrink} />
            <p>{drink.strInstructions}</p>
          </div>
        ))}
      </div>
      <div>
        <button onClick={goToPrevPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={goToNextPage} disabled={currentPage === totalPages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default Drink;
