import React, { useState } from 'react';
import './drinkCard.css';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

const Drink = ({ drink, image }) => {
  const [showRecipe, setShowRecipe] = useState(false);

  const handleClick = () => {
    setShowRecipe((prevState) => !prevState);
  };


  return (
    <div className="drink-container">
      <div className='drink-item'>

      <OverlayTrigger
      placement="bottom"
      overlay={<Tooltip id="button-tooltip-2">Check out this avatar</Tooltip>}
    >
      {({ ref, ...triggerHandler }) => (
        <Button
          variant="light"
          {...triggerHandler}
          className="d-inline-flex align-items-center"
        >
          <Image
            ref={ref}
            roundedCircle
            src="holder.js/20x20?text=J&bg=28a745&fg=FFF"
          />
          <span className="ms-1">Hover to see</span>
        </Button>
      )}
    </OverlayTrigger>



        <img src={drink.strDrinkThumb} alt={drink.strDrink} onClick={handleClick} />
      <h2 className='drink-name'>{drink.strDrink}</h2>
      <h3>Ingredients:</h3>
              <p>{getIngredients(drink).join(', ')}</p>
      {showRecipe && (
        <div className="popup2">
          <div className="ingredients">
            <h4>Ingredients:</h4>
            <ul>
              {getIngredientsWithMeasures(drink).map((ingredientWithMeasure, index) => (
                <li key={index}>{ingredientWithMeasure}</li>
              ))}
            </ul>
          </div>
          <h4>Directions:</h4>
          <p>{drink.strInstructions}</p>
        </div>
      )}
      </div>

    </div>
  );
};

export default Drink;

// Helper functions
function getIngredientsWithMeasures(drink) {
  const ingredientsWithMeasures = [];
  for (let i = 1; i <= 15; i++) {
    const ingredient = drink['strIngredient' + i];
    const measure = drink['strMeasure' + i];

    if (ingredient && measure) {
      ingredientsWithMeasures.push(`${measure} ${ingredient}`);
    } else if (ingredient) {
      ingredientsWithMeasures.push(ingredient);
    }
  }
  return ingredientsWithMeasures;
}

function getIngredients(drink) {
  const ingredients = [];
  for (let i = 1; i <= 15; i++) {
    const ingredient = drink['strIngredient' + i];

    if (ingredient) {
      ingredients.push(ingredient);
    }
  }

  return ingredients
}
