import React, { useState } from 'react';
import './drinkCard.css';

const Drink = ({ drinkName, image }) => {
  const [showRecipePopup, setShowRecipePopup] = useState(false);

  const handleImageClick = () => {
    setShowRecipePopup(true);
  };

  const closePopup = () => {
    setShowRecipePopup(false);
  };

  return (
    <div className="drink-item">
      <h2>{drinkName}</h2>
      <img src={image} alt={drinkName} onClick={handleImageClick} />

      {showRecipePopup && (
        <div className="popup">
          <h3>{drinkName}</h3>
          {/* You can display the recipe here */}
          {/* For example: */}
          <p>Recipe: Lorem ipsum dolor sit amet, consectetur adipiscing elit...</p>
          <button onClick={closePopup}>Close</button>
        </div>
      )}
    </div>
  );
};

export default Drink;
