import React, { useState, useEffect } from 'react';
import serviceApi from './services/Api'; // Import your serviceApi
import Slider from 'react-slick';
import Footer from './components/Footer';
import Drink from './components/DrinkCard';

import "./App.css"
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';


const App = () => {
  const [drinks, setDrinks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState(''); // New state for the search term
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const drinksPerPage = 20;
  const totalDrinks = 600; // Set the total number of drinks (if known)
  const breakpoint = 768
  const fetchDrinks = () => {
    const offset = (currentPage - 1) * drinksPerPage;

    // Use the serviceApi.searchDrinks function if a searchTerm is provided, otherwise, use serviceApi.getAll
    const apiCall = searchTerm ? serviceApi.searchDrinks(searchTerm) : serviceApi.getAll({ limit: drinksPerPage, offset });

    apiCall
      .then((response) => {
        const newDrinks = response.drinks || [];
        setDrinks(newDrinks);
      })
      .catch((error) => {
        console.error('Error fetching drinks:', error);
        setDrinks([]);
      });
  };

  useEffect(() => {
    fetchDrinks();
  }, [currentPage, searchTerm]); // Include currentPage and searchTerm in the dependency array

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, Math.ceil(totalDrinks / drinksPerPage)));
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // const handleSearch = () => {
  //   // When the search term changes, fetch drinks with the new search term and reset to page 1
  //   setCurrentPage(1);
  //   fetchDrinks();
  // };

  const carouselSettings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 5, // Number of drinks shown at once
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768, // Adjust the breakpoint based on your design and device
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <div className='drinksPage'>
      {/* Search input and button */}
      <div className='drink-search'>
        <input
          type="text"
          placeholder="Enter drink name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {/* <button onClick={handleSearch}>Search</button> */}
      </div>
      <div>
      {windowWidth < breakpoint ? ( // Check if the window width is greater than the breakpoint
        <Slider {...carouselSettings}>
          {drinks.map((drink, index) => (
            <div className='dink-carousel' key={index}>
              <Drink drink={drink} />
            </div>
          ))}
        </Slider>
      ) : (
        // Render the regular layout when window width is smaller than or equal to the breakpoint
        drinks.map((drink, index) => (
          <div className='drink-container'>
          <Drink key={index} drink={drink} />
          </div>
        ))
      )}
    </div>
      {/* <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={handleNextPage} disabled={currentPage === Math.ceil(totalDrinks / drinksPerPage)}>
          Next Page
        </button>
      </div> */}
      <Footer />
    </div>
  );
};

export default App;
