import React, { useState, useEffect } from 'react';
import serviceApi from './services/Api';
import Footer from './components/Footer.js';
import Drink from './components/DrinkCard';

const App = () => {
  const [drinks, setDrinks] = useState([]);
  const drinksPerPage = 25;
  const totalDrinks = 25; // Set the total number of drinks (if known)

  useEffect(() => {
    fetchAllDrinks();
  }, []); // Fetch drinks on component mount

  const fetchAllDrinks = async () => {
    const allDrinks = [];
    let currentPage = 1;

    while (allDrinks.length < totalDrinks) {
      const response = await serviceApi.getAll({ limit: drinksPerPage, offset: (currentPage - 1) * drinksPerPage });
      const newDrinks = response.drinks;

      if (newDrinks.length === 0) {
        // If no more drinks are available, break out of the loop
        break;
      }

      allDrinks.push(...newDrinks);
      currentPage++;
    }

    setDrinks(allDrinks);
  };

  return (
    <div>
      <Drink drinks={drinks} />
      <Footer />
    </div>
  );
};

export default App;
