import React, { useState, useEffect } from 'react';
import serviceApi from './services/Api';
import Footer from './components/Footer.js';
import Drink from './components/DrinkCard';

const App = () => {
  const [drinks, setDrinks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const drinksPerPage = 25;

  useEffect(() => {
    fetchDrinks();
    addScrollListener();
    return () => removeScrollListener();
  }, []); // Fetch drinks on component mount and add/remove scroll listener

  const fetchDrinks = () => {
    const offset = (currentPage - 1) * drinksPerPage;
    serviceApi.getAll({ limit: drinksPerPage, offset }).then((response) => {
      const newDrinks = response.drinks;
      setDrinks((prevDrinks) => [...prevDrinks, ...newDrinks]); // Append new drinks to existing drinks
    });
  };

  const handleScroll = () => {
    // Fetch more drinks when user reaches the end of the page
    if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const addScrollListener = () => {
    window.addEventListener('scroll', handleScroll);
  };

  const removeScrollListener = () => {
    window.removeEventListener('scroll', handleScroll);
  };

  return (
    <div>
      <Drink drinks={drinks} />
      <Footer />
    </div>
  );
};

export default App;
