import React, { useState } from 'react';
import './drinkCard.css';
import Button from 'react-bootstrap/Button';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

const Drink = ({ drink }) => {
  const [showRecipe, setShowRecipe] = useState(false);

  const handleClick = () => {
    setShowRecipe((prevState) => !prevState);
  };

  const renderTooltip = (props) => (
    <Tooltip id="button-tooltip" {...props}>
      Click to see the recipe
    </Tooltip>
  );

  return (
    <div className="drink-container">
      <div className='drink-item'>
        <img src={drink.strDrinkThumb} alt={drink.strDrink} onClick={handleClick} />
        <h2 className='drink-name'>{drink.strDrink}</h2>
        <h3>Ingredients:</h3>
        <p>{getIngredients(drink).join(', ')}</p>

        {/* Bootstrap-React OverlayTrigger */}
        <OverlayTrigger
          placement="right"
          delay={{ show: 250, hide: 400 }}
          overlay={renderTooltip}
        >
          <Button variant="success" onClick={handleClick}>
            {showRecipe ? 'Hide Recipe' : 'Show Recipe'}
          </Button>
        </OverlayTrigger>

        {/* Popup */}
        {showRecipe && (
          <div className="popup">
            {/* ... (Popup content) ... */}
          </div>
        )}
      </div>
    </div>
  );
};

// ... (Helper functions and export)

export default Drink;
