import serviceApi from './services/Api';
import Footer from './components/Footer.js';
import Drink from './components/DrinkCard';


const App = () => {
  const [drinks, setDrinks] = useState([]);

  useEffect(() => {
    serviceApi.getAll().then((response) => {
      const allDrinks = response.drinks; // Access the 'drinks' array within the response
      setDrinks(allDrinks); // Update the state with the fetched drinks
    });
  }, []) // Empty dependency array since it's a one-time fetch

