import React, { useState } from 'react'
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Drinks from '../drinks/drinksPage'

import { Link } from 'react-router-dom';

const NavigationBar = () => {

  const handleSearch = (e) => {
    e.preventDefault();

    const searchTerm = e.target.searchTerm.value;
    console.log(searchTerm)
    if (searchTerm) {
        console.log(searchTerm)
        // Only navigate if the searchTerm is not empty
        return <Link to={`/cocktails/${searchTerm}`}></Link>; // Navigate to the desired route
      }
  };

  const [data, setData] = useState('');

  const handleClick = () => {
    setData('Hello from ParentComponent');
  };

  return (

    <Navbar bg="dark" variant="dark" expand="lg">
      <Navbar.Brand href="#home">My App</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="#about">About</Nav.Link>
          <Nav.Link href="cocktails">Cocktails</Nav.Link>
          <Nav.Link href="#contact">Contact</Nav.Link>
        </Nav>
        <Form
          className="d-flex"
          onSubmit={handleSearch}
        >
            <Form.Control
              type="search"
              placeholder="Search for cocktails"
              name="searchTerm"
              className="me-2"
              aria-label="Search"
            />

            <Button variant="outline-success" type="submit">
            Search
            </Button>
            <div>
            <button onClick={handleClick}>Click me</button>
      <ChildComponent data={data} />
            </div>
          </Form>
      </Navbar.Collapse>
    </Navbar>

  );


};

export default NavigationBar;
