import React from 'react';
import { ListGroup, Row, Col } from 'react-bootstrap'; // Import the ListGroup, Row, and Col components from react-bootstrap

const Drink = ({ drinks }) => {
  return (
    <Row>
      {drinks.map((drink, index) => (
        <Col key={index} lg={5} md={3} sm={1}>
          <ListGroup.Item>
            <h2>{drink.strDrink}</h2>
            <img src={drink.strDrinkThumb} alt={drink.strDrink} className="img-fluid" />
            <p>{drink.strInstructions}</p>
          </ListGroup.Item>
        </Col>
      ))}
    </Row>
  );
};

export default Drink;
