import React, { useState, useEffect, useCallback } from 'react';
import serviceApi from './services/Api';
import Footer from './components/Footer.js';
import Drink from './components/DrinkCard';

const App = () => {
  const [drinks, setDrinks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState(''); // New state for the search term
  const drinksPerPage = 20;
  const totalDrinks = 600; // Set the total number of drinks (if known)

  const fetchDrinks = useCallback(() => {
    const offset = (currentPage - 1) * drinksPerPage;
    serviceApi.getAll({ limit: drinksPerPage, offset })
    .then((response) => {
      const newDrinks = response.drinks;
      setDrinks(newDrinks); // Update the drinks for the current page
    });
  }, [currentPage, drinksPerPage]);

  useEffect(() => {
    fetchDrinks();
  }, [fetchDrinks]); // Include fetchDrinks in the dependency array

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, Math.ceil(totalDrinks / drinksPerPage)));
  };

  const handleSearch = () => {
    // Fetch drinks based on the search term
    serviceApi.searchDrinks(searchTerm)
      .then((response) => {
        const newDrinks = response.drinks;
        setDrinks(newDrinks); // Update the drinks with the search results
        setCurrentPage(1); // Reset the current page to 1 after the search
      })
      .catch((error) => {
        console.error('Error searching drinks:', error);
        setDrinks([]); // Clear the drinks if there's an error
      });
  };

  return (
    <div>
      {/* Search input and button */}
      <div>
        <input
          type="text"
          placeholder="Enter drink name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <Drink drinks={drinks} />

      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={handleNextPage} disabled={currentPage === Math.ceil(totalDrinks / drinksPerPage)}>
          Next Page
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default App;
