import React, { useState, useEffect } from 'react';
import serviceApi from './services/Api';
import Footer from './components/Footer.js';
import Drink from './components/DrinkCard';

const App = () => {
  const [drinks, setDrinks] = useState([]);
  const drinksPerPage = 25;
  const totalDrinks = 600; // Set the total number of drinks (if known)

  useEffect(() => {
    fetchAllDrinks();
  }, []); // Fetch drinks on component mount

  const fetchAllDrinks = () => {
    const allDrinks = [];
    const fetchNextPage = (currentPage) => {
      const offset = (currentPage - 1) * drinksPerPage;
      serviceApi.getAll({ limit: drinksPerPage, offset }).then((response) => {
        const newDrinks = response.drinks;

        if (newDrinks && newDrinks.length > 0) {
          allDrinks.push(...newDrinks);
          if (allDrinks.length < totalDrinks) {
            fetchNextPage(currentPage + 1); // Fetch the next page if more drinks are available
          } else {
            // All drinks fetched, update state with allDrinks
            setDrinks(allDrinks);
          }
        } else {
          // Handle the case when no more drinks are returned from the API
          setDrinks(allDrinks);
        }
      });
    };

    fetchNextPage(1); // Start fetching from page 1
  };

  return (
    <div>
      <Drink drinks={drinks} />
      <Footer />
    </div>
  );
};

export default App;
